import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends Component {
   state = {
       number: 6,
       className : "btn btn-primary"
   };


  increment= () =>{
    const newClassName = "btn btn-danger"
    const newNumber = this.state.number+1;
    const numState = { number : newNumber, className : newClassName};
    this.setState(numState);

  }
  decrement= () =>{

    const number = this.state.number;
    if (number === 0) {
       const newClassName = "btn btn-secondary";
       const newState = {number : this.state.number, className : newClassName}
       this.setState(newState)

    }
    else{
      const newNumber = this.state.number-1;
      const numState = { number : newNumber, className : "btn btn-primary" };
      this.setState(numState);
    }



  }
  render() {
    return (
      <div>
       <button type="button" class= {this.state.className} onClick={this.decrement}>-</button>
      {this.state.number}
      <button type="button" class= {this.state.className} onClick={this.increment}>+</button>
      </div>


    );
  }
}
export default App;
