import  { Component } from "react";
 import 'bootstrap/dist/css/bootstrap.min.css';
import Box from './box.js'

class App extends Component {
    state = {
        boxes : [ ]
    }
    createBox = ()=>{
        const { boxes } =this.state;
        this.setState({boxes: [...boxes, 0]})

    }
     deleteBox = ()=>{
        const { boxes } =this.state;
        this.setState({boxes: []})

    } 
   
    
    render (){
        return (
             <div style = { {margin: '50px'}}>
                <button className="btn btn-danger mb-5"onClick={this.createBox}>add New Box</button>
                {this.state.boxes.map((number,index)=>(
                    <div key={number}>
                        <Box num={number}/>
                        <br/>

                 
                    </div>

                
            
                ))}
                 <br/>
               <button className="btn btn-danger mb-5"onClick={this.deleteBox}>Delete Box</button> 
            </div> 
          

        )

        
    }
}

    

export default App;
